package com.job5156.core.common.util;

import sun.misc.BASE64Decoder;
import sun.misc.BASE64Encoder;

import java.io.*;

public class CreateImageUtils {

    public static String GetImageStr(String imgFilePath) {// 将图片文件转化为字节数组字符串，并对其进行Base64编码处理
        byte[] data = null;

        // 读取图片字节数组
        try {
            InputStream in = new FileInputStream(imgFilePath);
            data = new byte[in.available()];
            in.read(data);
            in.close();
        } catch (IOException e) {
            e.printStackTrace();
        }

        // 对字节数组Base64编码
        BASE64Encoder encoder = new BASE64Encoder();
        return encoder.encode(data);// 返回Base64编码过的字节数组字符串
    }

    public static boolean GenerateImage(String imgStr, String imgFilePath,String imgFileName) {// 对字节数组字符串进行Base64解码并生成图片

        if (imgStr == null) return false; // 图像数据为空

        BASE64Decoder decoder = new BASE64Decoder();
        //创建文件保存路径
        try {
            File file = new File(imgFilePath);
            //如果目录不存在则创建目录
            if (!file.exists()) file.mkdirs();
        } catch (Exception e) {
            e.printStackTrace();
        }
        try {
            // Base64解码
            byte[] bytes = decoder.decodeBuffer(imgStr);
            for (int i = 0; i < bytes.length; ++i) {
                if (bytes[i] < 0) { // 调整异常数据
                    bytes[i] += 256;
                }
            }
            // 生成jpeg图片
            OutputStream out = new FileOutputStream(imgFilePath + System.getProperty("file.separator") + imgFileName);
            out.write(bytes);
            out.flush();
            out.close();
            return true;
        } catch (Exception e) {
            e.printStackTrace();
            return false;
        }
    }

    public static void main(String[] args) {
        // 测试从Base64编码转换为图片文件
        String strImg = "";
        GenerateImage(strImg, "D:\\", "test.jpg");

        // 测试从图片文件转换为Base64编码
        System.out.println(GetImageStr("d:\\test.jpg"));
    }

}