DROP TABLE `pos_brief`;
CREATE TABLE `pos_brief` (
  `id` int(11) unsigned NOT NULL,
  `posId` int(11) NOT NULL,
  `posType` tinyint(4) NOT NULL,
  `posName` varchar(200) DEFAULT NULL,
  `comId` int(11) NOT NULL,
  `comName` varchar(100) DEFAULT NULL,
  `comDesc` longtext,
  `deptId` int(11) DEFAULT NULL,
  `deptName` varchar(50) DEFAULT NULL,
  `prodId` int(11) DEFAULT NULL,
  `prodName` varchar(255) DEFAULT NULL,
  `srvTime` datetime DEFAULT NULL,
  `srvExtend` varchar(1000) DEFAULT NULL,
  `jobLocPC1` int(10) DEFAULT NULL COMMENT '地区对应编号',
  `jobLocPC2` int(10) DEFAULT NULL COMMENT '地区对应编号',
  `jobLocPC3` int(10) DEFAULT NULL COMMENT '地区对应编号',
  `workedYear` tinyint(4) DEFAULT NULL,
  `degreeID` tinyint(4) DEFAULT NULL,
  `ageUp` tinyint(4) DEFAULT NULL,
  `ageDown` tinyint(4) DEFAULT NULL,
  `gender` tinyint(4) DEFAULT NULL,
  `posDesc` mediumtext,
  `needHunt` tinyint(1) DEFAULT NULL,
  `lat` decimal(9,6) DEFAULT NULL COMMENT '纬度',
  `lng` decimal(9,6) DEFAULT NULL COMMENT '经度',
  `freDate` datetime DEFAULT NULL,
  `postDate` datetime DEFAULT NULL,
  `updateDate` datetime DEFAULT NULL,
  `posFlag` tinyint(4) DEFAULT NULL,
  `jsoninfo` longtext,
  `jobCode1` smallint(5) DEFAULT NULL,
  `jobCode2` smallint(5) DEFAULT NULL,
  `jobCode3` smallint(5) DEFAULT NULL,
  `hot_degree` int(10) unsigned DEFAULT '0' COMMENT '热度(数据来源用户应聘次数)',
  PRIMARY KEY (`id`),
  KEY `IX_pos_brief_posId` (`posId`),
  KEY `IX_pos_brief_posType` (`posType`),
  KEY `IX_pos_brief_comId` (`comId`),
  KEY `IX_pos_brief_deptId` (`deptId`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

DROP TABLE `com_brief`;
CREATE TABLE `com_brief` (
  `id` int(10) NOT NULL,
  `jsoninfo` longtext NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

DROP TABLE `resume_brief`;
CREATE TABLE `resume_brief` (
  `id` int(10) NOT NULL COMMENT '简历信息ID',
  `account_id` int(10) DEFAULT '0' COMMENT '个人用户帐号ID',
  `resume_name` varchar(100) DEFAULT NULL COMMENT '简历名称',
  `resume_type` tinyint(2) DEFAULT NULL COMMENT '简历类型',
  `resume_id` int(10) DEFAULT NULL COMMENT '简历ID  中文简历 和 英文简历相互对应',
  `language_type` tinyint(2) DEFAULT '0' COMMENT '语言类型:0为中文，1为英文',
  `user_last_edit_date` datetime DEFAULT NULL COMMENT '用户的最后修改时间',
  `fre_date` datetime DEFAULT NULL COMMENT '简历刷新时间',
  `resume_grade` smallint(5) DEFAULT '0' COMMENT '简历类型：0-普通,1-精英(猎头) 2-模具人才. 101:中高级人才（进查询库）  105:中高级人才(保密级不进查库)',
  `pass_date` datetime DEFAULT NULL COMMENT '审核时间',
  `come_from` int(10) DEFAULT NULL COMMENT '简历来源',
  `have_photo` tinyint(2) DEFAULT '0' COMMENT '是否有照片：0为无，1为有',
  `pass` smallint(5) DEFAULT '0' COMMENT '-12：被后台用户删除，-11：被用户自己删除，-10:自杀式简历，不让登录，-9：封锁,-3：审核不合格，-2：过滤状态，-1：未开通，0:未完善，1：暂时开通，10：已开通',
  `read_count` int(10) DEFAULT NULL COMMENT '被企业查看次数  ',
  `res_formwork` tinyint(2) DEFAULT NULL COMMENT '简历模板：0-6 默认为0',
  `perfect_num` double(10,5) DEFAULT NULL COMMENT '简历完整度数',
  `perfect_info` tinyint(2) DEFAULT '0' COMMENT '简历是否完整',
  `create_date` datetime DEFAULT NULL COMMENT '个人用户的创建时间',
  `login_date` datetime DEFAULT NULL COMMENT '登录时间',
  `public_settings` tinyint(2) DEFAULT '0' COMMENT '简历保密　0:完全公开　1:完全保密　2:公开但联系方式保密',
  `user_name` varchar(100) DEFAULT NULL COMMENT '个人用户简历名称',
  `gender` tinyint(2) DEFAULT NULL COMMENT '性别：1为男，2为女',
  `birthday` date DEFAULT NULL COMMENT '出生日期',
  `location` int(10) unsigned DEFAULT '0' COMMENT '地区对应编号',
  `location_town` int(10) unsigned DEFAULT '0' COMMENT '地区对应编号',
  `email` varchar(100) DEFAULT NULL COMMENT '电子邮箱',
  `card_type` tinyint(2) DEFAULT NULL COMMENT '证件类型',
  `card_num` varchar(100) DEFAULT NULL COMMENT '证件号码',
  `hometown` int(10) unsigned DEFAULT '0' COMMENT '地区对应编号',
  `hometown_town` int(10) unsigned DEFAULT '0' COMMENT '地区对应编号',
  `phone` varchar(100) DEFAULT NULL COMMENT '固定电话',
  `mobile` varchar(100) DEFAULT NULL COMMENT '手机号码',
  `nation` varchar(100) DEFAULT NULL COMMENT '民族',
  `marriage` tinyint(2) DEFAULT NULL COMMENT '婚姻状况所对应的编码',
  `stature` smallint(5) DEFAULT NULL COMMENT '身高',
  `weight` smallint(5) DEFAULT NULL COMMENT '体重',
  `zipcode` varchar(100) DEFAULT NULL COMMENT '邮编',
  `mobile_activation` tinyint(2) DEFAULT NULL COMMENT '手机激活',
  `identification` tinyint(2) DEFAULT NULL COMMENT '实名认证',
  `mail_activation` tinyint(2) DEFAULT NULL COMMENT '邮件激活',
  `address` varchar(250) DEFAULT NULL COMMENT '地址,格式为{"prov":"省","city":"市","dist":"区","addr":"地址"}',
  `homepage` varchar(100) DEFAULT NULL COMMENT '个人主页',
  `qq` varchar(100) DEFAULT NULL COMMENT 'QQ',
  `political` tinyint(2) DEFAULT NULL COMMENT '政治面貌',
  `jobyear_type` tinyint(2) DEFAULT NULL COMMENT '工作年限类型',
  `job_state` tinyint(2) DEFAULT NULL COMMENT '求职状态类型',
  `now_salary` tinyint(2) DEFAULT NULL COMMENT '目前薪水',
  `file_name` varchar(100) DEFAULT NULL COMMENT '头像的文件名称',
  `file_path` varchar(100) DEFAULT NULL COMMENT '头像的文件路径',
  `jsoninfo` longtext,
  `mobile_place` varchar(128) DEFAULT NULL COMMENT '手机号码归属地Json字符串',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

DROP TABLE `resume_export_brief`;
CREATE TABLE `resume_export_brief` (
  `id` int(11) NOT NULL,
  `resume_id` int(10) NOT NULL COMMENT '简历ID  中文简历 和 英文简历相互对应',
  `user_name` varchar(100) DEFAULT NULL COMMENT '姓名',
  `gender` tinyint(2) DEFAULT NULL COMMENT '性别：1=男，2=女',
  `birthday` date DEFAULT NULL COMMENT '出生日期',
  `age` int(2) DEFAULT NULL COMMENT '年龄',
  `degree` int(10) DEFAULT NULL COMMENT '最高学历',
  `mobile` varchar(100) DEFAULT NULL COMMENT '手机',
  `email` varchar(100) DEFAULT NULL COMMENT '邮箱',
  `work_year` tinyint(2) DEFAULT NULL COMMENT '工作年限',
  `address` varchar(500) DEFAULT NULL COMMENT '详细地址',
  `create_date` date NOT NULL COMMENT '创建日期',
  `update_date` date NOT NULL COMMENT '更新日期',
  `fre_date` date DEFAULT NULL COMMENT '刷新时间',
  `pass` tinyint(2) NOT NULL DEFAULT '0' COMMENT '简历状态，''-12：被后台用户删除，-11：被用户自己删除，-10:自杀式简历，不让登录，-9：封锁,-3：审核不合格，-2：过滤状态，-1：未开通，0:未完善，1：暂时开通，10：已开通''',
  `location` int(10) unsigned DEFAULT '0' COMMENT '地区对应编号',
  `job_location1` int(10) DEFAULT NULL COMMENT '意向地区编号',
  `job_location2` int(10) DEFAULT NULL COMMENT '意向地区编号',
  `job_location3` int(10) DEFAULT NULL COMMENT '意向地区编号',
  `job_code1` smallint(5) DEFAULT NULL COMMENT '期望工作职位1',
  `job_code2` smallint(5) DEFAULT NULL COMMENT '期望工作职位2',
  `job_code3` smallint(5) DEFAULT NULL COMMENT '期望工作职位3',
  `account_from` int(10) DEFAULT NULL,
  `resume_from` int(10) DEFAULT NULL,
  `login_date` date DEFAULT NULL COMMENT '登陆日期',
  `account_id` int(10) DEFAULT NULL COMMENT '账户id',
  `language_type` tinyint(2) DEFAULT NULL COMMENT '语言类别',
  `default_flag` tinyint(2) DEFAULT NULL COMMENT '是否为默认简历，1=是，0=否',
  PRIMARY KEY (`id`),
  KEY `I_account_id` (`account_id`) USING BTREE,
  KEY `I_language_type` (`language_type`) USING BTREE,
  KEY `I_default_flag` (`default_flag`) USING BTREE
) ENGINE=MyISAM DEFAULT CHARSET=utf8;


