DROP TABLE `per_invite_log`;
CREATE TABLE `per_invite_log` (
  `id` int(10) NOT NULL AUTO_INCREMENT COMMENT '请面试邀主键',
  `per_user_id` int(10) NOT NULL COMMENT '个人ID',
  `com_id` int(10) NOT NULL COMMENT '企业ID',
  `pos_id` int(10) NOT NULL COMMENT '职位ID',
  `cre_time` datetime DEFAULT NULL COMMENT '请邀时间',
  `edit_time` datetime DEFAULT NULL,
  `del_status` tinyint(2) DEFAULT '0' COMMENT '个人删除字段',
  `read_status` tinyint(2) DEFAULT NULL COMMENT '是否已查看',
  `mark` varchar(100) DEFAULT NULL COMMENT '旗子标记 红：red  绿：green  蓝：blue',
  `remark` varchar(500) DEFAULT NULL COMMENT '备注内容',
  `user_name` varchar(100) DEFAULT NULL,
  `invite_detail` text COMMENT '邀请详情',
  `interview_address` varchar(500) DEFAULT NULL COMMENT '面试地点',
  `room_num` int(10) DEFAULT NULL COMMENT '频视面试房间号',
  `book_period` varchar(100) DEFAULT NULL COMMENT '面试的预约时间段',
  `contact_person` varchar(100) DEFAULT NULL COMMENT '联系人',
  `contact_phone` varchar(100) DEFAULT NULL COMMENT '联系电话',
  `mix_info` longtext COMMENT 'json格式的混合信息',
  PRIMARY KEY (`id`),
  KEY `userid_index` (`per_user_id`),
  KEY `IX_pos_id` (`pos_id`),
  KEY `IX_cre_time` (`cre_time`)
) ENGINE=MyISAM AUTO_INCREMENT=7656873 DEFAULT CHARSET=utf8;

DROP TABLE `com_interview`;
CREATE TABLE `com_interview` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `com_id` int(10) NOT NULL,
  `per_user_id` int(10) NOT NULL,
  `resume_id` int(10) NOT NULL,
  `pos_id` int(10) NOT NULL,
  `user_name` varchar(100) DEFAULT NULL,
  `pos_name` varchar(100) DEFAULT NULL COMMENT '职位名称',
  `gender` tinyint(2) DEFAULT NULL,
  `age` tinyint(2) DEFAULT NULL,
  `degree` tinyint(2) DEFAULT NULL COMMENT '学历',
  `school_name` varchar(100) DEFAULT NULL COMMENT '校学名称',
  `location` int(10) unsigned DEFAULT '0' COMMENT '现所在地',
  `speciality` varchar(100) DEFAULT NULL COMMENT '专业',
  `job_location` varchar(100) DEFAULT NULL COMMENT '希望工作地区',
  `email` varchar(100) DEFAULT NULL COMMENT '邮箱号',
  `mix_info` text COMMENT '混合信息',
  `apply_numer` tinyint(2) DEFAULT NULL COMMENT '应聘数次',
  `mark` varchar(100) DEFAULT NULL COMMENT '不符合/符合/待定/已面试/待邀请/已邀请/自定义标签',
  `remark` varchar(500) DEFAULT NULL COMMENT '评语内容',
  `sys_recommend` int(10) DEFAULT '0' COMMENT '1后台推荐的简历',
  `read_flag` tinyint(2) DEFAULT NULL COMMENT '阅读状态1已阅读',
  `del_status` tinyint(2) DEFAULT '0' COMMENT '-1已删除',
  `receive_type` tinyint(2) DEFAULT '0' COMMENT '类型（1为视频,其它为网络应聘）',
  `send_flag` tinyint(2) DEFAULT NULL COMMENT '信息类型(1:到公司面试，2：到场馆面试 3：视频面试 4：侍考虑 5：婉拒 6:自动回复）',
  `buy_flag` tinyint(2) DEFAULT '0' COMMENT '是否购买了此份简历（0:否 1：是）',
  `create_date` datetime DEFAULT NULL COMMENT '创建时间',
  `update_date` datetime DEFAULT NULL COMMENT '更新时间',
  `jobyear_type` int(10) DEFAULT NULL COMMENT '工作经验',
  `keywords` varchar(500) DEFAULT NULL COMMENT '简历关键字',
  `start_date` datetime DEFAULT NULL COMMENT '面试开始时间',
  `end_date` datetime DEFAULT NULL COMMENT '面试结束时间',
  `invite_detail` text COMMENT '邀请详情',
  `interview_address` varchar(500) DEFAULT NULL COMMENT '面试地址',
  `bus_line` varchar(500) DEFAULT NULL COMMENT '乘车路线',
  `market_num` varchar(100) DEFAULT NULL COMMENT '展位号',
  `room_num` int(10) DEFAULT NULL COMMENT '视频面试房间号',
  `contact_person` varchar(100) DEFAULT NULL COMMENT '面试联系人',
  `contact_phone` varchar(100) DEFAULT NULL COMMENT '联系电话',
  `filter_flag` tinyint(4) DEFAULT '0' COMMENT '简历是否被过滤（0:否 1：是）',
  `match_rat` float DEFAULT '0' COMMENT '简历匹配度 -百分比显示',
  PRIMARY KEY (`id`),
  KEY `I_comid` (`com_id`),
  KEY `I_perid` (`per_user_id`),
  KEY `I_resumeid` (`resume_id`),
  KEY `idx_comid` (`com_id`) USING BTREE,
  KEY `idx_peruserId` (`per_user_id`) USING BTREE,
  KEY `idx_resume_id` (`resume_id`) USING BTREE,
  KEY `idx_pos_id` (`pos_id`) USING BTREE,
  KEY `idx_user_name` (`user_name`) USING BTREE,
  KEY `idx_comid_resumeId` (`com_id`,`resume_id`) USING BTREE,
  KEY `idx_email` (`email`) USING BTREE,
  KEY `idx_createDate` (`create_date`) USING BTREE,
  KEY `idx_location` (`location`) USING BTREE,
  KEY `idx_update_date` (`update_date`) USING BTREE
) ENGINE=MyISAM AUTO_INCREMENT=7603686 DEFAULT CHARSET=utf8;

