  KEY `idx_resume_id` (`resume_id`) USING BTREE,
  KEY `idx_pos_id` (`pos_id`) USING BTREE,
  KEY `idx_user_name` (`user_name`) USING BTREE,
  KEY `idx_comid_resumeId` (`com_id`,`resume_id`) USING BTREE,
  KEY `idx_email` (`email`) USING BTREE,
  KEY `idx_createDate` (`create_date`) USING BTREE,
  KEY `idx_location` (`location`) USING BTREE
) ENGINE=MyISAM AUTO_INCREMENT=104259 DEFAULT CHARSET=utf8;

DROP TABLE `com_forget_password`;
CREATE TABLE `com_forget_password` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `com_id` int(10) NOT NULL DEFAULT '0' COMMENT '企业id',
  `com_name` varchar(100) NOT NULL COMMENT '企业名称',
  `email` varchar(100) NOT NULL COMMENT '邮箱号',
  `contact_person` varchar(100) NOT NULL COMMENT '联系人',
  `telephone` varchar(100) NOT NULL,
  `mobile` varchar(100) NOT NULL COMMENT '企业手机号',
  `remark` varchar(500) DEFAULT NULL COMMENT '备注内容',
  `create_date` datetime NOT NULL COMMENT '创建时间',
  `update_date` datetime NOT NULL COMMENT '更新时间',
  `reply_date` datetime DEFAULT NULL COMMENT '回复时间',
  `reply_person` varchar(20) DEFAULT NULL COMMENT '回复人',
  `reply_mobile` varchar(100) DEFAULT NULL,
  `reply_content` text,
  `reply_flag` tinyint(2) NOT NULL DEFAULT '-1' COMMENT '处理方式-1=未回复，0=邮件回复，1=短信回复，2=口述回复，3=回复提交人，4=申请中断，5=回复提交人，6=邮件+短信回复',
  `reply_person_id` int(10) DEFAULT NULL COMMENT '处理人id',
  `password_info` varchar(500) DEFAULT NULL COMMENT '密码信息',
  `flag` int(10) DEFAULT NULL COMMENT '提示状态',
  PRIMARY KEY (`id`),
  KEY `I_com_id` (`com_id`)
) ENGINE=MyISAM AUTO_INCREMENT=6289 DEFAULT CHARSET=utf8;

DROP TABLE `com_forget_password_reply`;
CREATE TABLE `com_forget_password_reply` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `user_id` int(10) NOT NULL COMMENT '回复用户id',
  `forget_id` int(10) NOT NULL COMMENT '找回密码申请id',
  `reply_content` text NOT NULL COMMENT '回复内容',
  `create_date` datetime NOT NULL COMMENT '回复日期',
  `reply_flag` tinyint(2) NOT NULL DEFAULT '0' COMMENT '回复类型，0=处理者回复 1=申请者回复',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=6605 DEFAULT CHARSET=utf8;

DROP TABLE `com_interview_result`;
CREATE TABLE `com_interview_result` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `com_id` int(11) NOT NULL DEFAULT '0',
  `per_user_id` int(11) DEFAULT NULL,
  `pos_name` varchar(100) DEFAULT NULL COMMENT '职位名称',
  `notify_type` tinyint(2) DEFAULT NULL COMMENT '通知类别  1手机 2邮箱 ',
  `create_date` datetime DEFAULT NULL,
  `uodate_date` datetime DEFAULT NULL,
  `interview_address` varchar(500) DEFAULT NULL COMMENT '面试地址',
  `interview_date` datetime DEFAULT NULL COMMENT '面试日期',
  `interview_result` tinyint(2) NOT NULL DEFAULT '0' COMMENT '1通过 0洽谈中 -1未通过',
  `interviewer` varchar(100) DEFAULT NULL COMMENT '面试官',
  `remark` varchar(500) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

DROP TABLE `com_msg_log`;
CREATE TABLE `com_msg_log` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `com_id` int(10) NOT NULL,
  `per_user_id` int(10) NOT NULL,
  `com_account` varchar(100) NOT NULL COMMENT '企业账号',
  `receiver` varchar(100) NOT NULL COMMENT '接收人',
  `msg_type` tinyint(2) NOT NULL COMMENT '短信类型 0:手机短信 1视频面试 2场馆面试 3到企业面试',
  `msg_content` varchar(500) NOT NULL COMMENT '短信内容',
  `interview_address` varchar(500) NOT NULL COMMENT '面试地址',
  `room_num` int(10) DEFAULT NULL COMMENT '视频面试房间号',
  `reserve_date` datetime DEFAULT NULL COMMENT '预约时间',
  `create_date` datetime NOT NULL COMMENT '创建日期=发送日期',
  `update_date` datetime DEFAULT NULL,
  `resume_id` int(10) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `idx_com_id` (`com_id`),
  KEY `idx_com_account` (`com_account`),
  KEY `idx_per_user_id` (`per_user_id`),
  KEY `idx_resume_id` (`resume_id`),
  KEY `idx_receiver` (`receiver`),
  KEY `idx_msg_type` (`msg_type`),
  KEY `idx_reserve_date` (`reserve_date`),
  KEY `idx_create_date` (`create_date`),
  KEY `idx_update_date` (`update_date`)
) ENGINE=MyISAM AUTO_INCREMENT=1219527 DEFAULT CHARSET=utf8;

