  `del_status` tinyint(2) DEFAULT '0' COMMENT '-1已删除',
  `receive_type` tinyint(2) DEFAULT '0' COMMENT '类型（1为视频,其它为网络应聘）',
  `send_flag` tinyint(2) DEFAULT NULL COMMENT '发送标志1-邀请面试，2-待考虑，3-婉言谢绝',
  `buy_flag` tinyint(2) DEFAULT '0' COMMENT '是否购买了此份简历（0:否 1：是）',
  `create_date` datetime DEFAULT NULL COMMENT '创建时间',
  `update_date` datetime DEFAULT NULL COMMENT '更新时间',
  `jobyear_type` int(10) DEFAULT NULL COMMENT '工作经验',
  `keywords` varchar(500) DEFAULT NULL COMMENT '简历关键字',
  `filter_flag` tinyint(4) DEFAULT '0' COMMENT '简历是否被过滤（0:否 1：是）',
  `match_rat` float DEFAULT '0' COMMENT '简历匹配度 -百分比显示',
  PRIMARY KEY (`id`),
  KEY `com_id_index` (`com_id`),
  KEY `per_user_id_index` (`per_user_id`),
  KEY `I_resume_id` (`resume_id`),
  KEY `idx_comid` (`com_id`) USING BTREE,
  KEY `idx_peruserId` (`per_user_id`) USING BTREE,
  KEY `idx_resume_id` (`resume_id`) USING BTREE,
  KEY `idx_pos_id` (`pos_id`) USING BTREE,
  KEY `idx_user_name` (`user_name`) USING BTREE,
  KEY `idx_comid_resumeId` (`com_id`,`resume_id`) USING BTREE,
  KEY `idx_email` (`email`) USING BTREE,
  KEY `idx_createDate` (`create_date`) USING BTREE,
  KEY `idx_location` (`location`) USING BTREE,
  KEY `idx_update_date` (`update_date`) USING BTREE
) ENGINE=MyISAM AUTO_INCREMENT=13171037 DEFAULT CHARSET=utf8;

DROP TABLE `log_access_pos`;
CREATE TABLE `log_access_pos` (
  `id` int(10) NOT NULL AUTO_INCREMENT COMMENT '主键ID',
  `pos_id` int(10) DEFAULT NULL COMMENT ' 职位ID',
  `com_id` int(10) DEFAULT NULL COMMENT '企业ID',
  `user_id` int(10) DEFAULT NULL COMMENT '用户ID',
  `user_name` varchar(30) DEFAULT NULL COMMENT '用户姓名',
  `user_ip` varchar(30) DEFAULT NULL COMMENT '用户IP',
  `user_agent` varchar(250) DEFAULT NULL COMMENT '客户端信息',
  `create_date` datetime DEFAULT NULL COMMENT '创建日期',
  `pos_type` tinyint(4) DEFAULT NULL COMMENT '职位类型[1-网络职位 2-招聘会职位]',
  `channel` varchar(20) DEFAULT NULL COMMENT '访问通道',
  PRIMARY KEY (`id`),
  KEY `IX_posId` (`pos_id`),
  KEY `IX_createDate` (`create_date`),
  KEY `IX_comId_posType` (`com_id`,`pos_type`)
) ENGINE=MyISAM AUTO_INCREMENT=230780537 DEFAULT CHARSET=utf8;

DROP TABLE `per_pos_apply`;
CREATE TABLE `per_pos_apply` (
  `id` int(10) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `per_user_id` int(10) NOT NULL COMMENT '人个用户ID',
  `pos_id` int(10) NOT NULL COMMENT '职位ID',
  `com_id` int(10) NOT NULL COMMENT '企业ID',
  `apply_num` tinyint(2) DEFAULT NULL,
  `cre_time` datetime NOT NULL COMMENT '应聘时间',
  `edit_time` datetime DEFAULT NULL,
  `reply_status` tinyint(2) DEFAULT NULL COMMENT '企业对此次应聘的回复状态',
  `del_status` tinyint(2) DEFAULT NULL,
  `mark` varchar(100) DEFAULT NULL COMMENT '旗子标记  红：read  绿：green  蓝：blue',
  `remark` varchar(200) DEFAULT NULL COMMENT '备注内容',
  `mix_info` longtext COMMENT 'json格式的混合信息',
  `from_source_url` varchar(800) DEFAULT NULL COMMENT '来源地址',
  `spread_item_tn` varchar(30) DEFAULT NULL COMMENT '推广栏目编号',
  `resume_id` int(10) DEFAULT NULL COMMENT '应聘的简历ID',
  `resume_name` varchar(100) DEFAULT NULL COMMENT '应聘的简历名称',
  `resume_type` tinyint(2) DEFAULT '0' COMMENT '简历类型:0：普通简历 1: 为长沙类型的简历 2: 名片',
  PRIMARY KEY (`id`),
  KEY `userid_index` (`per_user_id`),
  KEY `IX_pos_id` (`pos_id`),
  KEY `IX_resume_id` (`resume_id`),
  KEY `IX_cre_time` (`cre_time`)
) ENGINE=MyISAM AUTO_INCREMENT=33761954 DEFAULT CHARSET=utf8;

DROP TABLE `per_pos_favorite`;
CREATE TABLE `per_pos_favorite` (
  `id` int(10) NOT NULL AUTO_INCREMENT COMMENT '键主ID',
  `per_user_id` int(10) NOT NULL COMMENT '个人用户ID',
  `pos_id` int(10) NOT NULL COMMENT '职位ID',
  `com_id` int(10) NOT NULL COMMENT '企业ID',
  `del_status` tinyint(2) DEFAULT NULL COMMENT '0未删除 -1 已删除',
  `cre_time` datetime NOT NULL COMMENT '收藏时间',
  `edit_time` datetime DEFAULT NULL,
  `mark` varchar(100) DEFAULT NULL COMMENT '旗子 红：red 绿：green 蓝：blue',
  `remark` varchar(500) DEFAULT NULL COMMENT '注备内容',
  `mix_info` longtext COMMENT 'json格式的混合信息',
  PRIMARY KEY (`id`),
  KEY `userid_index` (`per_user_id`)
) ENGINE=MyISAM AUTO_INCREMENT=4787233 DEFAULT CHARSET=utf8;

DROP TABLE `per_resume_refresh`;
CREATE TABLE `per_resume_refresh` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `user_id` int(11) DEFAULT NULL COMMENT '用户id',
  `refresh_time` datetime DEFAULT NULL COMMENT '刷新时间',
  `from_source_url` varchar(800) DEFAULT NULL COMMENT '来源地址',
  `spread_item_tn` varchar(100) DEFAULT NULL COMMENT '推广栏目编号',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=2022894 DEFAULT CHARSET=gbk COMMENT='简历刷新表';

DROP TABLE `per_sendout`;
CREATE TABLE `per_sendout` (
  `id` int(10) NOT NULL AUTO_INCREMENT COMMENT '外发简历主键ID',
  `per_user_id` int(10) NOT NULL COMMENT '个人用户账号ID',
  `resume_name` varchar(100) NOT NULL COMMENT '简历名称',
  `out_email` varchar(100) NOT NULL COMMENT '外发邮箱地址',
  `com_name` varchar(100) NOT NULL COMMENT '企业名称',
  `pos_name` varchar(100) NOT NULL COMMENT '职位名称',
  `remark` varchar(100) DEFAULT NULL COMMENT '其他要求/说明',
  `email_format` tinyint(2) NOT NULL COMMENT '邮件格式【0简体1繁体】',
  `cre_date` datetime NOT NULL COMMENT '外发时间',
  `del_status` tinyint(2) DEFAULT NULL COMMENT '0未删除 -1已删除',
  PRIMARY KEY (`id`),
  KEY `userid_index` (`per_user_id`)
) ENGINE=MyISAM AUTO_INCREMENT=3278919 DEFAULT CHARSET=utf8;

DROP TABLE `per_view_log`;
CREATE TABLE `per_view_log` (
  `id` int(10) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `per_user_id` int(10) NOT NULL COMMENT '人个账号ID',
  `com_id` int(10) NOT NULL COMMENT '企业ID',
  `look_num` int(10) DEFAULT NULL COMMENT '查看次数',
  `cre_time` datetime DEFAULT NULL COMMENT '查看简历时间',
  `edit_time` datetime DEFAULT NULL COMMENT '修改时间（修改备注）',
  `look_status` tinyint(2) DEFAULT NULL COMMENT '是否查看联系方式  未查看0   已查看1',
  `del_status` tinyint(2) DEFAULT NULL COMMENT '删除  未删除0   已删除-1',
  `mark` tinyint(2) DEFAULT NULL COMMENT '旗子标记',
  `remark` varchar(500) DEFAULT NULL COMMENT '注备内容',
  `com_name` varchar(100) DEFAULT NULL,
  `com_info` varchar(500) DEFAULT NULL,
  `mix_info` longtext COMMENT 'json格式的混合信息',
  PRIMARY KEY (`id`),
  KEY `userid_index` (`per_user_id`),
  KEY `delstatus_index` (`del_status`),
  KEY `I_com_id` (`com_id`) USING BTREE,
  KEY `I_cre_time` (`cre_time`) USING BTREE,
  KEY `I_edit_time` (`edit_time`) USING BTREE
) ENGINE=MyISAM AUTO_INCREMENT=45874711 DEFAULT CHARSET=utf8;

DROP TABLE `site_message`;
CREATE TABLE `site_message` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `come_from` varchar(100) NOT NULL COMMENT '消息来源类别(COM:表示来自企业,PER:表示来自个人,SYS:表示来自系统)',
  `com_id` int(10) DEFAULT NULL COMMENT '企业ID',
  `per_user_id` int(10) DEFAULT NULL COMMENT '个人ID',
  `per_user_name` varchar(100) DEFAULT NULL COMMENT '个人名称',
  `group_id` int(10) DEFAULT NULL COMMENT '分组ID,用做排序,(旧数据导入:replyId=0时为记录ID,否则等于replyId)',
  `source_id` int(10) DEFAULT NULL COMMENT '发件人ID，这里可能是企业、个人或系统',
  `source_name` varchar(100) DEFAULT NULL COMMENT '发件人名字,这里可能是企业、个人或系统',
  `photo` varchar(100) DEFAULT NULL COMMENT '头像',
  `gender` tinyint(2) DEFAULT NULL COMMENT '性别',
  `age` smallint(4) DEFAULT NULL COMMENT '年龄',
  `degree` smallint(4) DEFAULT NULL COMMENT '学历',
  `category` varchar(100) DEFAULT NULL COMMENT '动态分组，用于首页分组显示，同一分组内可能有多种类型的动态',
  `type` varchar(100) DEFAULT NULL COMMENT '动态的类型，内容根据动态类型在程序中生成',
  `pos_id` int(10) DEFAULT NULL COMMENT '职位id',
  `pos_name` varchar(500) DEFAULT NULL COMMENT '职位名称',
  `title` varchar(100) DEFAULT NULL COMMENT '标题',
  `content` varchar(1024) DEFAULT NULL,
  `read_flag` tinyint(2) NOT NULL DEFAULT '0' COMMENT '是否已读(0为未读，1为已读)',
  `draft_flag` tinyint(2) NOT NULL DEFAULT '0' COMMENT '草稿(0.普通 1.草稿)',
  `per_del_flag` tinyint(2) NOT NULL DEFAULT '0' COMMENT ' 0:表示正常,-1:表示个人删除',
  `com_del_flag` tinyint(2) NOT NULL DEFAULT '0' COMMENT '0:表示正常,-1:表示企业删除..',
  `reply_flag` tinyint(2) NOT NULL DEFAULT '0' COMMENT ' 是否回复(0:未回复,1:已回复)',
  `public_flag` tinyint(2) NOT NULL DEFAULT '1' COMMENT '回复内容是否公开显示(0:不公开,1:默认公开)',
  `reply_id` int(10) NOT NULL DEFAULT '0' COMMENT '所回复的消息Id,默认为0表示首发而不是回复',
  `create_date` datetime NOT NULL COMMENT '创建日期',
  `update_date` datetime DEFAULT NULL COMMENT '更新日期',
  `resume_id` int(10) DEFAULT NULL,
  `com_user_id` int(10) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `com_id_idx` (`com_id`) USING BTREE,
  KEY `I_comefrom` (`come_from`),
  KEY `I_per_id` (`per_user_id`),
  KEY `idx_category` (`category`),
  KEY `idx_type` (`type`),
  KEY `idx_create_date` (`create_date`),
  KEY `IX_pos_id` (`pos_id`) USING BTREE
) ENGINE=MyISAM AUTO_INCREMENT=12899808 DEFAULT CHARSET=utf8;





